mapper.insert:
	INSERT INTO MEMBER 
    (
      ID                
    , NUM             
    , NAME           
    , PWD            
    , BIRTH          
    , SEX             
    , PHONE         
    , EMAIL          
    , PANALTY      
    , JOINDATE     
    , ENABLED      
    )
    VALUES
    (
      #{id}
    , member_seq.NEXTVAL
    , #{name}
    , #{pwd}
    , #{birth}
    , #{sex}
    , #{phone}
    , #{email}
    , '0'
    , sysdate
    , '1'
    )

mapper.delete:
DELETE FROM MEMBER 
 WHERE ID  = #{id}  
   AND PWD = #{pwd}

mapper.update:
UPDATE MEMBER 
   SET PWD   = #{pwd} 	
     , PHONE = #{phone}
     , EMAIL = #{email} 
 WHERE ID  = #{id}  
   AND PWD = #{pwd}

mapper.select:
SELECT 
           COUNT(*)
  FROM MEMBER
 WHERE ID  = #{id} 	
   AND PWD = #{pwd}
