mapper.insert:
INSERT INTO WORKBOARD
(
  NUM
, ID
, TITLE
, COMPENSATE
, NEEDPEOPLE
, PHONE
, CONTENT
, REGDATE
, REGPEOPLE
, CONFIRMPEOPLE
, ADDR
, STARTDATE
, ENDDATE
, EXPIRE
, STATE
)
VALUES
(
  WORK_SEQ.NEXTVAL
, #{id}
, #{title}
, #{compensate}
, #{needpeople}
, #{phone}
, #{content}
, SYSDATE
, 0
, 0
, #{addr}
, #{startdate}
, #{enddate}
, '1'
, '10'
)

mapper.update:
UPDATE WORKBOARD
  SET TITLE          = #{title}
    , COMPENSATE     = #{compensate}
    , NEEDPEOPLE     = #{needpeople}
    , PHONE          = #{phone}
    , CONTENT        = #{content}
    , ADDR           = #{addr}
    , STARTDATE      = #{startdate}
    , ENDDATE        = #{enddate}
WHERE num = #{num}

UPDATE WORKBOARD 
   SET EXPIRE    = '0' 
 WHERE NUM       = #{num}

UPDATE WORKBOARD
SET REGPEOPLE = REGPEOPLE + '1'
WHERE NUM = #{NUM}

mapper.select:
SELECT 
      COUNT(*)
 FROM WORKFORM
WHERE NUM = #{param1}
  AND WORKER = #{param2}
